package matrix

func Multiply(a, b [][]float64) [][]float64 {
    if len(a[0]) != len(b) {
        panic("invalid dimensions for matrix multiplication")
    }

    result := make([][]float64, len(a))
    for i := range result {
        result[i] = make([]float64, len(b[0]))
        for j := range b[0] {
            for k := range a[0] {
                result[i][j] += a[i][k] * b[k][j]
            }
        }
    }
    return result
}
